function getRandomColor() {
  const letters = '0123456789ABCDEF';
  let color = '#';
  for (let i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
}

//Race date may be time zone specific - check later
let races = new Array;
for (let i = 0; i < scedule.MRData.total; i++) {
	let racedate = new Date(scedule.MRData.RaceTable.Races[i].date);
	if(racedate > Date.now()){
		break;
	}
	races.push(scedule.MRData.RaceTable.Races[i].Circuit.Location.country);
}

let driverData = new Array;

class driver {
	constructor(lastName, data){
		this.label = lastName;
		this.data = data;
		this.fill = false;
		this.backgroundColor = getRandomColor();
		this.borderColor = this.backgroundColor;

	}
	// addData(points){
	// 	data.push(points);
	// }
}


//			VERY INEFFICIENT REFACTOR			//
//create drivers
for(let i = 0; i<standings[0].MRData.total; i++) {
	let id = standings[0].MRData.StandingsTable.StandingsLists[0].DriverStandings[i].Driver.familyName;
 	let points = new Array;
 	points.push(standings[0].MRData.StandingsTable.StandingsLists[0].DriverStandings[i].points);
 	for(let j = 1; j < races.length; j++){
 		for(let k = 0; k < standings[0].MRData.total; k++){
 			if(standings[j].MRData.StandingsTable.StandingsLists[0].DriverStandings[k].Driver.familyName == id){
 				points.push(standings[j].MRData.StandingsTable.StandingsLists[0].DriverStandings[k].points);
 				break;
 			}
 		}
 	}
 	driverData.push(new driver(id, points));
}

